import{_ as a,V as e,W as i,X as h}from"./framework-1e9ccf99.js";const d={},r=h('<h1 id="个体的学习风格差异" tabindex="-1"><a class="header-anchor" href="#个体的学习风格差异" aria-hidden="true">#</a> 个体的学习风格差异</h1><blockquote><p><img src="https://cdn.jsdelivr.net/gh/passwordgloo/icon/info.svg" alt="" loading="lazy">学习风格指学习者在完成学习任务时表现出来一贯的、典型的、独居个人特色的学习策略和学习倾向</p></blockquote><h2 id="学习风格的生理因素" tabindex="-1"><a class="header-anchor" href="#学习风格的生理因素" aria-hidden="true">#</a> 学习风格的生理因素</h2><h3 id="生理因素" tabindex="-1"><a class="header-anchor" href="#生理因素" aria-hidden="true">#</a> 生理因素</h3><h4 id="生理刺激" tabindex="-1"><a class="header-anchor" href="#生理刺激" aria-hidden="true">#</a> 生理刺激</h4><ul><li>声音</li><li>光线</li><li>温度</li><li>时间</li></ul><h4 id="时间节律" tabindex="-1"><a class="header-anchor" href="#时间节律" aria-hidden="true">#</a> 时间节律</h4><h4 id="感觉通道" tabindex="-1"><a class="header-anchor" href="#感觉通道" aria-hidden="true">#</a> 感觉通道</h4><ul><li>视觉型</li><li>听觉型</li><li>动觉型</li></ul><h4 id="大脑的单侧化" tabindex="-1"><a class="header-anchor" href="#大脑的单侧化" aria-hidden="true">#</a> 大脑的单侧化</h4><h2 id="学习风格的心理因素" tabindex="-1"><a class="header-anchor" href="#学习风格的心理因素" aria-hidden="true">#</a> 学习风格的心理因素</h2><h3 id="场独立型和场依存型" tabindex="-1"><a class="header-anchor" href="#场独立型和场依存型" aria-hidden="true">#</a> 场独立型和场依存型</h3><p>人的知觉受环境因素影响大称之为<code>场依存</code></p><p>人的知觉受环境因素影响小称之为<code>场独立</code></p><h3 id="冲动型和沉思型" tabindex="-1"><a class="header-anchor" href="#冲动型和沉思型" aria-hidden="true">#</a> 冲动型和沉思型</h3><p>冲动型学生一直有一种迅速确认事物情况的欲望，往往犯的错误多些</p><p>沉思型学生则采取谨慎小心的态度，做出的选择比较精确</p><h3 id="深层加工和浅层加工" tabindex="-1"><a class="header-anchor" href="#深层加工和浅层加工" aria-hidden="true">#</a> 深层加工和浅层加工</h3><p>深层加工指深刻理解所学内容，将所学内容与更大的概念框架联结起来、</p><p>表层加工指记忆学习内容的表面信息，不将它们与更大的概念框架联结起来</p><h3 id="整体型与系列型" tabindex="-1"><a class="header-anchor" href="#整体型与系列型" aria-hidden="true">#</a> 整体型与系列型</h3><h3 id="分类风格" tabindex="-1"><a class="header-anchor" href="#分类风格" aria-hidden="true">#</a> 分类风格</h3><blockquote><p><img src="https://cdn.jsdelivr.net/gh/passwordgloo/icon/info.svg" alt="" loading="lazy">当个体知觉彼此相似的物体时所采用的标准</p></blockquote><h2 id="学习风格的社会性因素" tabindex="-1"><a class="header-anchor" href="#学习风格的社会性因素" aria-hidden="true">#</a> 学习风格的社会性因素</h2><ol><li>独立学习与结伴学习</li><li>竞争与合作</li><li>成人支持</li></ol>',25),n=[r];function l(t,c){return e(),i("div",null,n)}const s=a(d,[["render",l],["__file","2.2.html.vue"]]);export{s as default};
